const burger=()=>{const e=document.querySelector(".burger"),t=document.querySelector(".nav");e.addEventListener("click",i=>{i.preventDefault(),t.classList.toggle("active"),e.classList.toggle("active")})};burger();const swiper=new Swiper(".swiper-container",{direction:"horizontal",loop:!1,pagination:{el:".swiper-pagination"},navigation:{nextEl:".slider__btn-next",prevEl:".slider__btn-prev"},scrollbar:{el:".swiper-scrollbar"}}),footer=()=>{const e=document.querySelectorAll(".footer__col-title");document.querySelectorAll(".footer__col-items");e.forEach(e=>{e.addEventListener("click",()=>{if(e.classList.toggle("active"),e.nextElementSibling.classList.contains("active"))e.nextElementSibling.style.height="0px",e.nextElementSibling.addEventListener("transitionend",()=>{e.nextElementSibling.classList.remove("active")},{once:!0});else{e.nextElementSibling.classList.add("active"),e.nextElementSibling.style.height="auto";let t=e.nextElementSibling.clientHeight+"px";e.nextElementSibling.style.height="0px",setTimeout(()=>{e.nextElementSibling.style.height=t},0)}})})};footer();