const burger=()=>{const e=document.querySelector(".burger"),t=document.querySelector(".nav");e.addEventListener("click",o=>{o.preventDefault(),t.classList.toggle("active"),e.classList.toggle("active")})};burger();const swiper=new Swiper(".swiper-container",{direction:"horizontal",loop:!1,pagination:{el:".swiper-pagination"},navigation:{nextEl:".slider__btn-next",prevEl:".slider__btn-prev"},scrollbar:{el:".swiper-scrollbar"}}),footer=()=>{const e=document.querySelectorAll(".footer__col-title");document.querySelectorAll(".footer__col-items");e.forEach(e=>{e.addEventListener("click",()=>{e.classList.toggle("active"),e.closest(".footer__col").querySelector(".footer__col-items").classList.toggle("active")})})};footer();